#!/bin/bash######- Take INPUTs -######read -p 'URL of ZIP file: ' URLread -p 'ART_NAME -the last name of the URL without .zip extension- is: ' ART_NAMEread -p 'Do you want to change the IP? [y/n]: ' INPUTINPUT=$(echo "$INPUT" | tr '[:upper:]' '[:lower:]')if [ "$INPUT" = "y" ]; then    read -p 'Network interface (e.g., enp0s8): ' NETWORKIF    read -p 'New IP: ' NIP    input_file="/etc/sysconfig/network-scripts/ifcfg-$NETWORKIF"    sudo cat "$input_file"fi######- Variables Declaration -######PACKAGE="wget unzip httpd"SVC="httpd"TEMPDIR="/tmp/webfiles"FREERAM=$(free -m | grep Mem | awk '{print $4}')LOAD=$(uptime | awk '{print $9}')ROOTFREE=$(df -h | grep '/dev/sda1' | awk '{print $4}')######- System info -######echo "Welcome $USER on $HOSTNAME"echo "##############################"echo "Available free RAM: $FREERAM MB"echo "##############################"echo "Current Load Average: $LOAD"echo "##############################"echo "Free ROOT partition size: $ROOTFREE"echo "##############################"######- Install packages {wget, unzip, httpd} -######echo "##############################"echo "Installing Packages"echo "##############################"sudo yum install $PACKAGE -y > /dev/null######- Start & enable httpd -######echo "##############################"echo "Starting and Enabling HTTPD"echo "##############################"sudo systemctl start $SVCsudo systemctl enable $SVC######- Create Temp directory -######echo "##############################"echo "Creating Temp Directory - webfiles"echo "##############################"sudo mkdir -p $TEMPDIRcd $TEMPDIR######- Install the web -######echo "##############################"echo "Installing the web"echo "##############################"wget $URL -O $ART_NAME.zip > /dev/nullunzip $ART_NAME.zip > /dev/nullsudo cp -r $ART_NAME/* /var/www/html/######- Change the IP -######if [ "$INPUT" = "y" ]; then    echo "##############################"    echo "Changing the IP"    echo "##############################"    if [ ! -f "$input_file" ]; then        echo "Input file does not exist."        exit 1    fi    sudo sed -i '/^IPADDR/d' "$input_file"    sudo bash -c "echo 'IPADDR=$NIP' >> $input_file"    echo "Updated IPADDR in $input_file to $NIP"    sudo systemctl restart networkfi######- Clean UP -######echo "##############################"echo "Removing temporary files"echo "##############################"sudo rm -rf $TEMPDIRsudo systemctl restart $SVC######- Final Status -######sudo systemctl status $SVC | grep Activels /var/www/html/